mapper.insert:

    SELECT SEQ_IMAGE_NO.CURRVAL AS rvNo FROM DUAL

insert into IMAGE_TBL values (
    SEQ_IMAGE_NO.NEXTVAL, #{tableName}, #{imageGroupNo}, #{imageName}, #{imageRename}, #{imagePath}
)

insert into PAY_TBL values (
   SEQ_PAY_NO.NEXTVAL, #{rvDetailNo}, #{userId}, #{payId}, #{payMethod}, #{payAmount}, #{payCurrency}, SYSDATE, DEFAULT, DEFAULT
                           )

insert into PAY_CANCEL_TBL values (
      SEQ_P_CANCEL_NO.NEXTVAL, #{payNo}, SYSDATE, #{cancelAmount}, #{reasonNo}
)

mapper.update:
update RESERVATION_TBL
    set IS_RV_CANCEL = 'Y'
    where RV_NO = #{rvNo}

update PAY_TBL
    set IS_PAY_CANCEL = 'Y'
    where RV_DETAIL_NO = #{rvDetailNo}

mapper.select:
select RV_NO
from (
         select RV_NO
         from RESERVATION_TBL
         where USER_NO = #{userNo}
         order by RV_NO desc
     )
where ROWNUM = 1;

select RV_DETAIL_NO
from (
         select RV_DETAIL_NO
         from RESERVATION_DETAIL_TBL
         where RV_NO = #{rvNo}
         order by RV_DETAIL_NO desc
     )
where ROWNUM = 1;

select USER_NO
    from USER_TBL
    where USER_ID = #{userId, jdbcType=VARCHAR}

select COUNT(*)
    from RESERVATION_TBL
    where USER_NO = #{userNo}
